    // }
    // console.clear()
    //console.log(movies)

    return (
        <div className='row'>
            <h2>{title}</h2>
            {/* {console.clear()} */}
            {console.log(movies)}
            {movies === undefined
                ? <p>Loading </p>
                :
                <div className='row__posters'>
                    {movies.map(movie => {
                        <p>{movie.title}</p>
                        // <img src={`${base_url}${movie.poster_path}`} alt={movie.title} />
                    })}
                    </div>

            }
            {/* several row posters */}
            {/* Check if movies exists */}
        </div>
    )
}

export default Row
